/**
 * Assigns the node to all the refs passed in the argument.
 */
export default function mergeRefs(refs) {
  return value => {
    refs.forEach(ref => {
      if (typeof ref === 'function') {
        ref(value);
      } else if (ref !== null && typeof ref === 'object') {
        ref.current = value;
      }
    });
  };
}