            },
            "url": "/render-map?pageTitle={page.title}",
            "categories": [],
            "outputType": "block",
            "bodyType": "none",
            "aliases": [],
            "name": {
              "value": "Maps"
            },
            "key": "dynamic-macro-example"
          }
        ]
